import { useState, useEffect, useContext, useRef } from 'react';

import ContextInformation from "./ContextInformation";

function DeckBuilder() {
    const context = useContext(ContextInformation);
    const searchText = useRef(null);
    const [ searchResults, setSearchResults ] = useState([]);

    useEffect(() => {
        console.log(searchResults);
    }, [searchResults])

    const submitSearch = (e) => {
        e.preventDefault();
        async function performSearch() {
            return await fetch(`https://api.scryfall.com/cards/search?q=${searchText.current.value}`)
                        .then(result => result.json())
                        .then(json => setSearchResults(json.data));
        }

        performSearch();
    }

    const addToDeck = (e, index) => {
        e.preventDefault();
        const newCard = searchResults[index];
        const searchForCard = context.deck.filter(card => card.id === newCard.id)
        if(searchForCard)
        {
            console.log(searchForCard);
        } else {
            newCard.count = 1;
            context.setDeck([...context.deck, newCard]);
        }   
    }

    return(
        <div className="deck-builder">
            <form>
                <input type="text" ref={searchText} id="search-text" name="search-text" placeholder="Search Text" />
                <input type="submit" id="search-button" name="search-button" onClick={submitSearch} />
            </form>
            {context.deck.map(card => <p>{card.name} {card.type} x{card.count}</p>)}
            {searchResults.map((card, index) => {
                const imageUrl = card.image_uris ? card.image_uris.small : "https://imgs.smoothradio.com/images/191589?crop=16_9&width=660&relax=1&signature=Rz93ikqcAz7BcX6SKiEC94zJnqo=";
                return (<div>
                            <img src={imageUrl} alt={card.name} />
                            <p>{card.name} {card.type} {card.cmc}</p>
                            <p>{card.oracle_text}</p>
                            <button onClick={e => addToDeck(e, index)}>+</button>
                        </div>)})}
        </div>
    )
}

export default DeckBuilder;
